// Cambiar estilos con JavaScript
document.body.style.fontFamily = "Arial, sans-serif";
document.body.style.margin = "0";
document.body.style.padding = "0";

document.querySelector('header').style.backgroundColor = "#333";
document.querySelector('header').style.color = "#fff";
document.querySelector('header').style.padding = "10px";
document.querySelector('header').style.textAlign = "center";

document.getElementById('barra-navegacion').style.backgroundColor = "#444";
document.getElementById('barra-navegacion').style.textAlign = "center";
document.getElementById('barra-navegacion').style.padding = "10px 0";

document.querySelectorAll('nav a').forEach(link => {
    link.style.color = "#fff";
    link.style.textDecoration = "none";
    link.style.padding = "0 10px";
});

document.querySelectorAll('nav a').forEach(link => {
    link.addEventListener('mouseover', function() {
        this.style.color = "#ccc";
    });
    link.addEventListener('mouseout', function() {
        this.style.color = "#fff";
    });
});
